const wordBar = document.getElementById("wordBar");
let wordsArray = [];
let numOfWords = 7;
let length = 5;
const apiUrl = `https://random-word-api.herokuapp.com/word?number=${numOfWords}&length=${length}&lang=en`;
let line = "";
let currentKeyPosition = 0;
let cursor = 0;
let errors = 0;
let highPos = 1;
let startTime, endTime;
async function getWords() {
  try {
    const response = await fetch(apiUrl);
    wordsArray = await response.json();
    line = wordsArray.join(" ");
    displayWords();
  } catch (error) {
    console.log(error);
  }
}
function displayWords() {
  for (let i = 0; i < line.length; i++) {
    const character = document.createElement("li");
    character.setAttribute("id", i);
    if (line.charAt(i) === " ") {
      character.innerHTML = "&nbsp";
    } else {
      character.innerHTML = line.charAt(i);
    }
    wordBar.appendChild(character);
  }
  wordBar.getElementsByTagName("li")[currentKeyPosition].style.borderBottom =
    "2px solid cyan";
}

function highlightCurrent() {
  try {
    wordBar.getElementsByTagName("li")[highPos - 1].style.borderBottom = "none";
    wordBar.getElementsByTagName("li")[highPos].style.borderBottom =
      "2px solid cyan";
  } catch (error) {
    console.log(error);
  }
}

function displayInfo() {
  try {
    let time = Math.round(Date.now() / 1000);
    let timeElapsed = time - startTime;
    let wpm = Math.floor(line.length / 5 / (timeElapsed / 60));
    let acc = ((line.length - errors) / line.length) * 100;
    document.getElementById("speed").innerHTML = `${wpm} <span>wpm</span>`;
    document.getElementById("acc").innerHTML = `${acc.toPrecision(
      4
    )} <span>%</span>`;
  } catch (error) {
    console.log(error);
  }
}

function beep() {
  var snd = new Audio(
    "data:audio/wav;base64,"
  );
  snd.play();
}
document.addEventListener("keypress", (event) => {
  var typedChar = event.key;
  if (currentKeyPosition === 0) {
    startTime = Math.round(Date.now() / 1000);
  }
  highlightCurrent();
  if (typedChar === line.charAt(currentKeyPosition)) {
    wordBar.getElementsByTagName("li")[currentKeyPosition].style.color = "#666";
  } else {
    errors++;
    wordBar.getElementsByTagName("li")[currentKeyPosition].style.color =
      "#FF0000";
    beep();
  }
  currentKeyPosition++;
  cursor++;
  highPos++;
  if (cursor === line.length) {
    // endTime = Math.round(d.getTime()/1000);
    displayInfo();
    cursor = 0;
    currentKeyPosition = 0;
    errors = 0;
    highPos = 1;
    wordBar.innerHTML = "";
    getWords();
  }
});
getWords();
